import"./hoisted.17158c12.js";let c=new URLSearchParams(window.location.search).get("cctv");c===null&&(c="1",window.location.href=`${window.location.protocol}//${window.location.host}/statistics?cctv=${c}`);document.getElementById(`cctv-${c}`)?.classList.add("active");document.querySelector("#return-link").href=`/cctv/room?name=${c}`;document.querySelectorAll(".cctv-button").forEach(t=>{if(t.id==`cctv-${c}`)return;const o=t.id.at(-1);t.addEventListener("click",()=>{window.location.href=`${window.location.protocol}//${window.location.host}/statistics?cctv=${o}`})});document.querySelectorAll(".drawer-toggle").forEach(t=>t.addEventListener("change",o=>{document.querySelectorAll(".drawer-toggle").forEach(e=>{e.checked=t.checked})}));
